import React from 'react';
import { View, Text, FlatList, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { NativeRouter, Routes, Route, Navigate } from 'react-router-native';
import useJugadoresList from '../Hooks/useJugadoresList';
import{useNavigation } from '@react-navigation/native';

const JugadoresCard = () => {
 const { jugadores, isLoading, error } = useJugadoresList();
 const navigation = useNavigation();

 if (isLoading) {
 return <Text>Cargando usuarios...</Text>;
}

if (error) {
 return <Text>Error al obtener los usuarios: {error.message}</Text>;
}

const renderItem = ({ item }) => (
 <TouchableOpacity onPress={() => {}}>
 <Image source={{ uri: item.avatar_url}}style={styles.image}/>
<Text style={styles.title}>{item.login}</Text>
<Text>ID:{item.id}</Text>
<Text>URL:{item.html_url}</Text>
</TouchableOpacity>
);

return (
 <FlatList
 data={jugadores}
 renderItem={renderItem}
 keyExtractor={item => item.id.toString()}
/>
);
};

const styles = StyleSheet.create({
card:{
 flexDirection: 'row',
 backgroundColor: 'white',
 padding: 20,
 marginVertical: 8,
 marginHorizontal: 16,
 borderRadius: 10,
 alignItems: 'center',
 },
 image: {
 width: 50,
 height: 50,
 borderRadius: 25,
 marginRight: 20,
 },
 title: {
 fontSize: 18,
 fontWeight: 'bold',
 },
});

export default JugadoresCard;
