import React, { useState, useEffect } from 'react';
import { FlatList } from 'react-native';

const JugadoresCard = ({ jugadores }) => {
 return (
 <FlatList
 data={jugadores}
 renderItem={({ item }) => (
 <TouchableOpacity onPress={() => {}}>
 <Image source={{ uri: item.avatar_url}} style={styles.image}/>
 <Text style={styles.title}>{item.login}</Text>
<Text>ID: {item.id}</Text>
<Text>URL: {item.html_url}</Text>
</TouchableOpacity>
 )}
 keyExtractor={item => item.id.toString()}
/>
 );
};

const styles = StyleSheet.create({
image: {
 width: 50,
 height: 50,
 borderRadius: 25,
 marginRight: 20,
 },
 title: {
 fontSize: 18,
 fontWeight: 'bold',
 },
});

export default JugadoresCard;
