import React from 'react';
import { View, Text, FlatList, StyleSheet, Image } from 'react-native';
import useJugadoresList from '../Hooks/useJugadoresList';

const JugadoresCard = () => {
    const { jugadores, isLoading, error } = useJugadoresList();

    if (isLoading) {
        return <Text>Loading users...</Text>;
    }

    if (error) {
        return <Text>Error fetching users: {error.message}</Text>;
    }

    const renderItem = ({ item }) => (
        <View style={styles.card}>
            <Image source={{ uri: item.avatar_url }} style={styles.image} />
            <Text style={styles.title}>{item.login}</Text>
            <Text>ID: {item.id}</Text>
            <Text>URL: {item.html_url}</Text>
        </View>
    );

    return (
        <FlatList
            data={jugadores}
            renderItem={renderItem}
            keyExtractor={item => item.id.toString()}
        />
    );
};

const styles = StyleSheet.create({
    image: {
        width: 50, // Establece el ancho deseado
        height: 50, 
        borderRadius: 25, 
    },
    card: {
        backgroundColor: 'white',
        padding: 20,
        marginVertical: 8,
        marginHorizontal: 16,
        borderRadius: 10,
        flex:1
    },
    title: {
        fontSize: 18,
        fontWeight: 'bold',
    },
});

export default JugadoresCard;
