import React from 'react';
import { Text, View, Dimensions, StyleSheet } from 'react-native';
import { StatusBar } from 'expo-status-bar';
import NavBar from './NavBar';
import { NativeRouter, Routes, Route, Navigate } from 'react-router-native';
import Entrenador from './views/Entrenador';
import Arbitro from './views/Arbitro'; 
import Jugador from './views/Jugador'; 
import Federacion from './views/Federacion'; 

const Main = () => {
    const { width, height } = Dimensions.get('window');

    return (
        <NativeRouter>
            <View style={styles.container(width, height)}>
                <StatusBar style="light" />
                <NavBar />
                <Routes>
                    <Route path="/" element={<Jugador />} /> {/* Definimos la ruta de inicio */}
                    <Route path="/Entrenador" element={<Entrenador />} />
                    <Route path="/Arbitro" element={<Arbitro />} />
                    <Route path="/Jugador" element={<Jugador />} />
                    <Route path="/Federacion" element={<Federacion />} />
                    <Route path="*" element={<Navigate to="/" replace />} />
                </Routes>

            </View>
        </NativeRouter>
    );
};

const styles = StyleSheet.create({
    container: (width, height) => ({
        width,
        height,
        backgroundColor: 'black',
    }),
    bottomText: {
        marginTop: 90,
        textAlign: 'center',
    },
    text: {
        color: 'white',
        textAlign: 'center',
        fontSize: 20,
    }
})

export default Main;
