import React from 'react';
import { View, Text } from 'react-native';

const JugadorDetalle = ({ jugador }) => {
  return (
    <View>
      <Text style={styles.title}>{`${jugador.name.title} ${jugador.name.first} ${jugador.name.last}`}</Text>
      <Text>ID: {jugador.login.username}</Text>
      <Text>URL: {jugador.email}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
    card: {
        backgroundColor: 'white',
        marginVertical: 10,
        marginHorizontal: 15,
        padding: 10,
        borderRadius: 25,
        flex: 1
    },
    image: {
        width: 50,
        height: 50,
        borderRadius: 25,
        marginRight: 20,
    },
    title: {
        fontSize: 18,
        fontWeight: 'bold',
        color: 'black'
    },
    text: {
        color: 'black',
        fontSize: 13,
    }
});

export default JugadorDetalle;
