import React from 'react';
import { Text, View, Dimensions } from 'react-native';
import { StatusBar } from 'expo-status-bar';
import NavBar from './NavBar';
import { NativeRouter, Routes, Route, Navigate } from 'react-router-native';

// Placeholder component for the home route
const Home = () => (
    <Text style={{ color: 'white', textAlign: 'center', marginTop: 90, fontSize: 20 }}>
        Home
    </Text>
);

const Main = () => {
    const { width, height } = Dimensions.get('window');

    return (
        <NativeRouter> 
            <View style={{ width, height, backgroundColor: 'black' }}>
                <StatusBar style="light" />
                <NavBar />
                <Routes>
                    <Route path='/' element={<Home />} /> {/* Now rendering the Home component */}
                    <Route path='/signin' element={<Text style={{ color: 'white', textAlign: 'center' }}>Sign In</Text>} />
                    <Route path='*' element={<Navigate to='/' replace />} />
                </Routes>
                <Text style={{ color: 'white', textAlign: 'center', marginTop: 90, fontSize: 20 }}>DS</Text>
                <Text style={{ color: 'white', textAlign: 'center', fontSize: 20 }}>Sports Data</Text>
            </View>
        </NativeRouter>
    );
};

export default Main;
