import React, { useState, useEffect } from 'react';
import { FlatList, Text, Image, TouchableOpacity, StyleSheet } from 'react-native';
import useJugadoresList from '../Hooks/useJugadoresList';

const JugadoresCard = () => {
  const { jugadores, isLoading, error } = useJugadoresList();

  if (isLoading) {
    return (
      <Text>Cargando jugadores...</Text>
    );
  }

  if (error) {
    return (
      <Text>Error al cargar los jugadores: {error.message}</Text>
    );
  }

  return (
    <>
      <FlatList
        data={jugadores}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => { }}>
            {/* <Image source={{ uri: item.results.thumbnail }} style={styles.image} /> */}
            <Text style={styles.title}>{item.results.name}</Text>
            <Text>ID:{item.results.username}</Text>
            <Text>URL:{item.results.email}</Text>
          </TouchableOpacity>
        )}
        keyExtractor={item => item.id.toString()}
      />
      <Text style={{ marginTop: 30, color: 'white' }}>Ac√° estoy jugador</Text>
    </>
  );
};

const styles = StyleSheet.create({
  image: {
    width: 50,
    height: 50,
    borderRadius: 25,
    marginRight: 20,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default JugadoresCard;
