import React from 'react';
import { View, Text, StyleSheet, Image } from 'react-native';

const JugadorDetalle = ({ jugador, setJugadorSeleccionado }) => {
    const fechaNacimiento = new Date(jugador.dob.date).toLocaleDateString('en-GB');

    const handleBackToJugadores = () => {
        setJugadorSeleccionado(null);
    };

    return (
        <View style={styles.card}>
            {/* Resto del c√≥digo del componente */}
            <Pressable onPress={handleBackToJugadores} style={styles.backButton}>
                <Text style={styles.backButtonText}>Volver a Jugadores</Text>
            </Pressable>
        </View>
    );
};

const styles = StyleSheet.create({
    card: {
        backgroundColor: 'white',
        marginVertical: 10,
        marginHorizontal: 15,
        padding: 10,
        borderRadius: 25,
        flex: 1,
        borderWidth: 1,
        borderColor: 'gray',
        borderRadius: 25,
    },
    image: {
        width: 50,
        height: 50,
        borderRadius: 25,
        marginRight: 20,
        alignSelf: 'center',
    },
    title: {
        fontSize: 18,
        fontWeight: 'bold',
        color: 'black',
        textAlign: 'center',
    },
    text: {
        color: 'black',
        fontSize: 13,
        marginTop: 10
    },
});

export default JugadorDetalle;
